const observer=new IntersectionObserver((t=>{t.forEach((t=>{t.isIntersecting?t.target.classList.contains("hiddenLeft")?t.target.classList.add("showLeft"):t.target.classList.contains("hiddenRight")?t.target.classList.add("showRight"):t.target.classList.add("show"):t.target.classList.contains("showLeft")?t.target.classList.remove("showLeft"):t.target.classList.contains("showRight")?t.target.classList.remove("showRight"):t.target.classList.remove("show")}))})),hiddenElements=document.querySelectorAll(".hidden, .hiddenLeft, .hiddenRight");hiddenElements.forEach((t=>observer.observe(t)));